import React from "react";

export const kaizentext = {

  lightgraytext: {
    color: "I am a Web Development Instructor @ Intec Brussels.",
    background: "white"
  }
};

const ThemeContext = React.createContext(kaizentext.lightgraytext);
export default ThemeContext;
